import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
     faPhp, faReact, faJquery, faTailwind, faSql
} from "@fortawesome/free-brands-svg-icons";
export default function Home() {
    const [activeSection, setActiveSection] = useState('about');

    const renderContent = () => {
        switch (activeSection) {
            case 'about':
                return <div><h1 className="text-green-500 text-2xl justify-center">Professional Profile</h1>
                    <p>Computer Science Graduate with a creative mindset and a passion for Software Development. Acquired solid foundation in object-oriented programming with Java. Progressed into developing and integrating REST APIs using object-oriented PHP. Collaborative team player attuned to working in a fast-paced environment whilst providing excellent quality service. Described as ‘outstanding, friendly and attentive’ by customers. Independent and adaptive problem solver with a keen eye for detail and strong work ethic. Proactive and committed learner with an optimistic attitude. Confident decision maker and resilient self starter. </p></div>;
            case 'skills':
                return <div><h1 className="text-green-500 mt-5 mb-3 text-2xl">Skills</h1>
                <ul>

                <li><FontAwesomeIcon icon={faReact} size="2x" /></li>
                <li><FontAwesomeIcon icon={faJquery} size="2x" /></li>
                <li><FontAwesomeIcon icon={faPhp} size="2x" /></li>
                <li><FontAwesomeIcon icon={faTailwind} size="2x" /></li>
                <li><FontAwesomeIcon icon={faSql} size="2x" /></li>
                </ul>
                </div>;
            case 'projects':
                return <div><h1 className="text-green-500 mt-5 mb-3 text-2xl">Projects</h1></div>;
            default:
                return <div>Select a section to view content.</div>;
        }
    };
    return (
        <div class="flex">
            <img src="https://images.unsplash.com/photo-1689829538884-7a7cfabda004?q=80&w=3087&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" class="w-1/2 h-screen" />
            <div class="w-1/2 flex items-center">
                <div class="mx-5">
                    <div class="my-fixed">{renderContent()}</div>
                    <div>

                        <button class="mr-2 relative after:bg-white hover:text-green-500 after:absolute after:h-0.5 after:w-0 after:bottom-0 after:left-0 hover:after:w-full after:transition-all after:duration-300 cursor-pointer" onClick={() => setActiveSection('about')}>About</button>
                        <button class=" mx-2 relative after:bg-white hover:text-green-500 after:absolute after:h-0.5 after:w-0 after:bottom-0 after:left-0 hover:after:w-full after:transition-all after:duration-300 cursor-pointer" onClick={() => setActiveSection('skills')}>Skills</button>
                        <button class="mx-2 relative after:bg-white hover:text-green-500 after:absolute after:h-0.5 after:w-0 after:bottom-0 after:left-0 hover:after:w-full after:transition-all after:duration-300 cursor-pointer" onClick={() => setActiveSection('projects')}>Projects</button>

                    </div>

                </div>
            </div>
        </div>
    )
}
