import { useState } from "react";
export default function Home() {
 const [activeSection, setActiveSection] = useState('about');

  const renderContent = () => {
    switch (activeSection) {
      case 'about':
        return <div><h1 className="text-red-500 text-2xl justify-center">Professional Profile</h1>
                        <p>Computer Science Graduate with a creative mindset and a passion for Software Development. Acquired solid foundation in object-oriented programming with Java. Progressed into developing and integrating REST APIs using object-oriented PHP. Collaborative team player attuned to working in a fast-paced environment whilst providing excellent quality service. Described as ‘outstanding, friendly and attentive’ by customers. Independent and adaptive problem solver with a keen eye for detail and strong work ethic. Proactive and committed learner with an optimistic attitude. Confident decision maker and resilient self starter. </p></div>;
      case 'skills':
        return <div><h1 className="text-red-500 mt-5 mb-3 text-2xl">Skills</h1></div>;
      case 'projects':
        return <div><h1 className="text-red-500 mt-5 mb-3 text-2xl">Projects</h1></div>;

    }
  };
    return (
            <div class="flex">
                <img src="https://images.unsplash.com/photo-1689829538884-7a7cfabda004?q=80&w=3087&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" class="w-1/2 h-screen" />
                <div class="w-1/2 flex items-center">
                    <div class="content">
                        <div>{renderContent()}</div>
                        <div>
        <button onClick={() => setActiveSection('about')}>About</button>
        <button onClick={() => setActiveSection('skills')}>Skills</button>
        <button onClick={() => setActiveSection('projects')}>Projects</button>
      </div>

                    </div>
                </div>
            </div>      
    )
}
