import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
    faPhp, faReact, faHtml5,
} from "@fortawesome/free-brands-svg-icons";
export default function Home() {
    const [activeSection, setActiveSection] = useState('about');

    const renderContent = () => {
        switch (activeSection) {
            case 'about':
                return <div><h1 className="text-green-500 text-2xl justify-center">Professional Profile</h1>
                    <p>Computer Science Graduate with a creative mindset and a passion for Web Development. Acquired solid foundation in object-oriented programming with Java. Progressed into developing and integrating REST APIs using object-oriented PHP. Collaborative team player attuned to working in a fast-paced environment whilst providing excellent quality service. Described as ‘outstanding, friendly and attentive’ by customers. Independent and adaptive problem solver with a keen eye for detail and strong work ethic. Proactive and committed learner with an optimistic attitude. Confident decision maker and resilient self starter. </p></div>;
            case 'skills':
                return <div><h1 className="text-green-500 mt-5 mb-3 text-2xl">Skills</h1>

                </div>;
            case 'projects':
                return <div><h1 className="text-green-500 mt-5 mb-3 text-2xl">Projects</h1></div>;
            default:
                return <div>Select a section to view content.</div>;
        }
    };
    return (


            <div class="mx-20 my-20">


    <h1 class="text-green-500 text-3xl mt-5">About</h1>
        <li>First Class Bachelors Degree in Computer Science with Web Development</li>
        <li>Dedicated learner</li>
        <li>4+ Years experience in Customer Service</li>

        <div class="flex justify-center space-x-10">
        <FontAwesomeIcon icon={faReact} size="7x" />
        <FontAwesomeIcon icon={faPhp} size="7x" />
        <FontAwesomeIcon icon={faHtml5} size="7x" />
    </div>

</div>


    )
}
